class ShoppingCart:
    def __init__(self):
        self.items = []
        self.total = 0

    def add_item(self, item, price):
        self.items.append({'item': item, 'price': price})
        self.total += price

    def remove_item(self, item_name):
        for i, item in enumerate(self.items):
            if item['item'] == item_name:
                self.total -= item['price']
                del self.items[i]
                break

    def get_total(self):
        return self.total
