import kotlinx.serialization.SerialName

@Serializable
@Entity(tableName = "users")
data class User(
    @SerialName("user_id")
    @PrimaryKey
    val id: String,

    @SerialName("display_name")
    val name: String,

    @SerialName("email_address")
    val email: String,

    val isActive: Boolean = true,
    val createdAt: Long = System.currentTimeMillis()
)

