#!/bin/bash
set -euo pipefail

DATABASE_URL="postgres://localhost:5432/mydb"

function migrate() {
    echo "Running migrations..."
    psql "$DATABASE_URL" -f migrations/up.sql
    echo "Running rollback check..."
    psql "$DATABASE_URL" -f migrations/verify.sql
    echo "Done."
}

function seed() {
    echo "Seeding database..."
    psql "$DATABASE_URL" -f seeds/data.sql
}

migrate
